import { useState, useCallback, useEffect } from "react";

import Button from "../UI/Button.js";
import ListData from "./ListData.js";

const GetFetch = () => {
  const [peoples, setPeoples] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [img, setImg] = useState();

  const fetchHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://picsum.photos/200.jpg");

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();

      console.log(data);

      const loadedData = [];

      for (const key in data) {
        loadedData.push({
          id: key,
          title: data[key].title,
          array: data[key].array,
        });
      }

      // const transformedData = data.results.map((dataFetch) => {
      //   return {
      //     id: dataFetch.id,
      //     name: dataFetch.name,
      //     anything: dataFetch.anything,
      //   };
      // });
      console.log(loadedData);
      setPeoples(loadedData);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchHandler();
  }, [fetchHandler]);

  let content = <p>Found no data.</p>;

  if (peoples.length > 0) {
    content = <ListData peoples={peoples} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <div>
      <img src={img} alt="icons" />
      <Button onClick={fetchHandler} value="Fetch"></Button>
      <section>{content}</section>
    </div>
  );
};

export default GetFetch;
