import { useState, useCallback, useEffect } from "react";

// import Button from "../UI/Button.js";
// import ListData from "./ListData.js";

const GetFetch = () => {
  // const [peoples, setPeoples] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [img, setImg] = useState([]);
  // const [imagesArray, setImagesArray] = useState([]);

  const fetchHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://picsum.photos/v2/list");

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      // const data = await response.json();
      const imagesBlob = await response.blob();

      console.log(imagesBlob);

      const imageObjectURL = URL.createObjectURL(imagesBlob);
      setImg(imageObjectURL);

      // for (const key in imagesArray) {
      //   setImagesArray.push({
      //     id: key,
      //     title: imagesArray[key].width,
      //     array: imagesArray[key].height,
      //   });
      // }

      // const transformedData = data.results.map((dataFetch) => {
      //   return {
      //     id: dataFetch.id,
      //     name: dataFetch.name,
      //     anything: dataFetch.anything,
      //   };
      // });
      // console.log(loadedData);
      // setPeoples(loadedData);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchHandler();
  }, [fetchHandler]);

  let content = <p>Found no data.</p>;

  // if (peoples.length > 0) {
  //   content = <ListData peoples={peoples} />;
  // }

  // if (img.length > 0) {
  //   content = <img src={img} alt="icons" />;
  // }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <div>
      img.forEach(item => {<img src={img} alt="icons" />});
      {/* <Button onClick={fetchHandler} value="Fetch"></Button> */}
      <section>{content}</section>
    </div>
  );
};

export default GetFetch;
