import React, { useState, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";

const ImageInfo = ({ param }) => {
  const [data, setData] = useState([]);
  console.log(param, "param", 2131231231231231231231232);
  const fetchImage = useCallback(async () => {
    try {
      const res = await fetch(`https://picsum.photos/v2/list/?id=${param.id}`);
      const resData = await res.json();

      if (!res.ok) {
        throw new Error("Something went wrong!");
      }
      console.log(param, "param");
      console.log(resData);

      setData(resData);
    } catch (error) {
      console.log(error);
    }
  }, [param]);

  useEffect(() => {
    fetchImage();
  }, [fetchImage]);

  return (
    <div>
      {data.map((item) => {
        return (
          <div className="image-container" key={item.id}>
            <div>
              <img className="image-info" src={item.download_url} alt="" />
            </div>
          </div>
        );
      })}
      <div>
        <Link to={`/`}>FEATURED IMAGES</Link>
      </div>
    </div>
  );
};

export default ImageInfo;
