import { useState } from "react";

import Button from "../UI/Button.js";
import ListData from "./ListData.js";

const GetFetch = () => {
  const [peoples, setPeoples] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  async function fetchHandler() {
    const response = await fetch(
      "https://react-exercise-4bddf-default-rtdb.firebaseio.com/"
    );
    const data = await response.json();

    console.log(data);
    const loadedData = [];

    for (const key in data) {
      loadedData.push({
        id: key,
        title: data[key].title,
      });
    }

    // const transformedData = data.results.map((dataFetch) => {
    //   return {
    //     id: dataFetch.id,
    //     name: dataFetch.name,
    //     anything: dataFetch.anything,
    //   };
    // });
    console.log(loadedData);
    setPeoples(loadedData);
  }

  let content = <p>Found no data.</p>;

  if (peoples.length > 0) {
    content = <ListData peoples={peoples} />;
  }

  if (error) {
    content;
  }

  return (
    <div>
      <Button onClick={fetchHandler} value="Fetch">
        fetch
      </Button>
      <ListData peoples={peoples}></ListData>
    </div>
  );
};

export default GetFetch;
