import { useState } from "react";

import Button from "../UI/Button.js";
import ListData from "./ListData.js";

const GetFetch = () => {
  const [peoples, setPeoples] = useState([]);

  async function fetchHandler() {
    const response = await fetch(
      "https://react-exercise-4bddf-default-rtdb.firebaseio.com/objects/first.json"
    );
    const data = await response.json();

    const loadedData = [];

    for (const key in data) {
      loadedData.push({
        id: key,
        title: data[key].title,
      });
    }

    // const transformedData = data.results.map((dataFetch) => {
    //   return {
    //     id: dataFetch.id,
    //     name: dataFetch.name,
    //     anything: dataFetch.anything,
    //   };
    // });
    setPeoples(loadedData);
  }

  return (
    <div>
      <Button onClick={fetchHandler} value="Fetch">
        fetch
      </Button>
      <ListData peoples={peoples}></ListData>
    </div>
  );
};

export default GetFetch;
